function populateCardNumbers(){var e=document.querySelectorAll(".bonus-card__number");Array.from(e).forEach((e,t)=>{e.textContent=t+1})}populateCardNumbers(),document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".bonus-card__open-btn").forEach(function(t){t.addEventListener("click",function(){var e=t.closest(".bonus-card").querySelector(".bonus-card__info");e.classList.contains("hidden")?(e.classList.remove("hidden"),t.textContent="Hide info",t.classList.add("active")):(e.classList.add("hidden"),t.textContent="More info",t.classList.remove("active"))})})});